import { all } from "redux-saga/effects"
import  storageWatcher  from "../modules/storage/sagas";
import {profileWatcher} from "../modules/profile/sagas/profile-saga";
import headerWatcher from "../modules/header/sagas";

export function* globalWatcher() {
    yield all([storageWatcher(), profileWatcher(), headerWatcher()])
}
