import React, {useEffect} from "react";
import CustomButton from '../../../components/button'
import "antd/dist/antd.css";
import styled from 'styled-components';
import {profileConfig} from "../configs/profile-config";
import CustomForm from "../../../components/form";
import Items from "./items";
import photo from "../../../icons/user_men_logo.png";
import CustomAvatar from "../../../components/avatar";
import { UserOutlined } from '@ant-design/icons';
import { Row, Col } from 'antd';

interface InfoItem {
    email: string;
    name: string;
    phone: string;
    username: string;
    website: string;
}

interface ProfileProps {
    infos: InfoItem;
    modalIsVisible: boolean;
    infoLoading: boolean;
    showModal: () => void;
    hideModal: () => void;
    getInfo: () => void;
}

const ProfileContainer = styled.div
    `
    display: grid;
`
;

const Profile = (props: ProfileProps) => {

    const {
        infos,
        modalIsVisible,
        infoLoading,
        showModal,
        hideModal,
        getInfo
    } = props;

    useEffect(() => {
        getInfo();
    }, [getInfo]);

    return (
        <ProfileContainer>
            <Row>
                <Col span={5} push={4}>
                    <h3>{profileConfig.pageName}</h3>
                    <CustomForm>
                        <Items />
                    </CustomForm>
                    <div>
                        {infos.name}
                    </div>
                    <CustomButton
                        type="primary"
                        onClick = {showModal}
                    >
                        {profileConfig.buttonText}
                    </CustomButton>
                </Col>
                <Col span={5} pull={1}>
                    <CustomAvatar src={photo} icon={<UserOutlined />}  size={100} />
                </Col>
            </Row>
        </ProfileContainer>
    );
}

export default Profile;
