import React, {useEffect, useState} from "react";
import CustomButton from '../../../components/button'
import "antd/dist/antd.css";
import styled from 'styled-components';
import {profileConfig} from "../configs/profile-config";
import CustomForm from "../../../components/form";
import {Form} from "antd";
import photo from "../../../icons/avatar.png";
import CustomAvatar from "../../../components/avatar";
import { UserOutlined } from '@ant-design/icons';
import { Row, Col, Divider } from 'antd';
import Input from "../../../components/input";
import CustomFormItem from "../../../components/form-item";


interface InfoItem {
    name: string;
    surname: string;
    phone: string;
    password: string;
    id: string;
}

interface ProfileProps {
    infos: InfoItem;
    infoLoading: boolean;
    getUserInfoById: () => void;
}

const ProfileContainer = styled.div
    `
    display: grid;
`
;

export const Profile = (props: ProfileProps) => {

    console.log(props);

    const {
        infos,
        getUserInfoById
    } = props;

    useEffect(() => {
        getUserInfoById();
        console.log('useEffect')
    }, [getUserInfoById]);

    const [componentSize, setComponentSize] = useState<string>('default');
    const [userInputValue, setUserInputValue] = useState<string>('');
    const onFormLayoutChange = ({size}:{size: any}) => {
        setComponentSize(size);
    };
    const onChange = ({e}:{e:any}) => {
        console.log(e);
    };

    type LayoutType = Parameters<typeof Form>[0]['layout'];

    const sharedProps = {
        labelCol: {span: 4,},
        wrapperCol: {span: 14,},
        layout: 'horizontal',
        initialValues: {size: componentSize,},
        onValuesChange: {onFormLayoutChange},
        size: {componentSize}
    };

    const [form] = Form.useForm();

    useEffect(() => {
        console.log(form.getFieldsValue());
        form.setFieldsValue({
            "Name": infos.name,
            "Surname": infos.surname,
            "Number": infos.phone
        });
    }, [infos]);

    return (
        <ProfileContainer>
            <Divider orientation="center">Profile</Divider>
            <Row>
                <Col span={20} push={1}>
                    <StyledForm
                        form={form}
                    >
                        <CustomFormItem {...sharedProps} name="Name" label="Аватар">
                            <Input />
                        </CustomFormItem>
                        <CustomFormItem  {...sharedProps} name ={"Surname"}  label="Пользователь">
                            <Input name ={"Surname"} placeholder={"Place for surname"} />
                        </CustomFormItem>
                        <CustomFormItem {...sharedProps} name="Name" label="Пароль">
                            <Input />
                        </CustomFormItem>
                        <CustomFormItem {...sharedProps} name="Name" label="Адрес эл. почты">
                            <Input />
                        </CustomFormItem>
                        <CustomFormItem {...sharedProps} name={"Number"}  label="Телефон">
                            <Input name={"Number"} placeholder={"00000000000"}/>
                        </CustomFormItem>
                        <CustomFormItem {...sharedProps} name="Name" label="Пол">
                            <Input />
                        </CustomFormItem>
                        <CustomFormItem {...sharedProps} name="Name" label="Дата рождения">
                            <Input />
                        </CustomFormItem>
                        <CustomFormItem {...sharedProps} name="Name" label="Страна">
                            <Input />
                        </CustomFormItem>
                    </StyledForm>
                    <CustomButton
                        type="primary"
                    >{profileConfig.buttonText}
                    </CustomButton>
                </Col>
                <Col span={1} pull = {2}>
                    <CustomAvatar src={photo} icon={<UserOutlined />} shape={"square"} size={200} />
                </Col>
            </Row>
        </ProfileContainer>
    );
}

const StyledForm = styled(Form)`
    width: 75%;
    border: 2px solid black;
    border-radius: 3px;
`;
