import React, {useEffect} from "react";
import CustomButton from '../../../components/button'
import "antd/dist/antd.css";
import styled from 'styled-components';
import {profileConfig} from "../configs/profile-config";
import CustomForm from "../../../components/form";
import photo from "../../../icons/avatar.png";
import CustomAvatar from "../../../components/avatar";
import { UserOutlined } from '@ant-design/icons';
import { Row, Col, Divider } from 'antd';
import Input from "../../../components/input";
import {InputNumber} from "antd";
import CustomFormItem from "../../../components/form-item";

interface InfoItem {
    email: string;
    name: string;
    phone: string;
    username: string;
    website: string;
}

interface ProfileProps {
    infos: InfoItem;
    modalIsVisible: boolean;
    infoLoading: boolean;
    showModal: () => void;
    hideModal: () => void;
    getInfo: () => void;
}

const ProfileContainer = styled.div
    `
    display: grid;
`
;

const Profile = (props: ProfileProps) => {

    const {
        infos,
        modalIsVisible,
        infoLoading,
        showModal,
        hideModal,
        getInfo
    } = props;

    useEffect(() => {
        getInfo();
    }, [getInfo]);

    return (
        <ProfileContainer>
            <Divider orientation="center">Profile</Divider>
            <Row>
                <Col span={20} push={1}>
                    <CustomForm>
                        <CustomFormItem label="Input">
                            <Input />
                        </CustomFormItem>

                        <CustomFormItem label="InputNumber">
                            <InputNumber />
                        </CustomFormItem>
                        <CustomFormItem label="Button">
                            <CustomButton
                                type="primary"
                                onClick = {showModal}
                            >Button</CustomButton>
                        </CustomFormItem>
                    </CustomForm
                        >
                    <CustomForm>
                        <Divider orientation="left">Personal Information</Divider>
                        <CustomFormItem label="Input">
                            <Input />
                        </CustomFormItem>
                        <Row gutter={[16, 24]}>
                            Surname: <Input />
                        </Row>
                        <Row gutter={[16, 24]}>
                            Birthday: <Input />
                        </Row>
                        <Row gutter={[16, 24]}>
                            Phone: <Input />
                        </Row>
                        <Row gutter={[16, 24]}>
                            Email: <Input />
                        </Row>
                    </CustomForm>
                    <CustomButton
                        type="primary"
                        onClick = {showModal}
                    >
                        {profileConfig.buttonText}
                    </CustomButton>
                </Col>
                <Col span={1} pull = {2}>
                    <CustomAvatar src={photo} icon={<UserOutlined />} shape={"square"} size={200} />
                </Col>
            </Row>
        </ProfileContainer>
    );
}

export default Profile;
